mapper.update:
UPDATE user_info
set password=#{password}
WHERE user_id = #{user_id}

UPDATE user_info
set password=#{new_password}
WHERE user_id = #{user_id}

UPDATE user_info
set profileIcon_id=#{profileIcon_id}
WHERE user_id = #{user_id}

mapper.select:
SELECT COUNT(*)
	FROM user_info
	WHERE user_email =#{user_email}

SELECT COUNT(*)
	FROM user_info
	WHERE user_name = #{user_name}
	AND phone_number = #{phone_number};
